import React, { useState } from 'react';
import axios from 'axios';

function TransactionForm() {
  const [formData, setFormData] = useState({
    sender: '',
    receiver: '',
    title: '',
    description: ''
  });
  const [file, setFile] = useState(null); // State to hold the file

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]); // Store the selected file
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    data.append('sender', formData.sender);
    data.append('receiver', formData.receiver);
    data.append('title', formData.title);
    data.append('description', formData.description);
    data.append('file', file); // Append the file to FormData

    try {
      console.log("Sending data:", formData);
      
      // Submit transaction
      const response = await axios.post('http://localhost:5000/add_transaction', data, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      alert(response.data.message); // Transaction added response

      // Now, mine the new block
      const mineResponse = await axios.get('http://localhost:5000/mine');
      alert(mineResponse.data.message); // New block mined response

      // Delay for 1 second, then reload the page
      setTimeout(() => {
        window.location.reload();
      }, 1000);
      
    } catch (error) {
      console.error("There was an error submitting the transaction!", error);
    }
  };

  return (
    <div className="submit-container">
      <h1 className="form-title">Upload File</h1>
      <form onSubmit={handleSubmit} className="submit-form">
        <input
          type="text"
          name="sender"
          placeholder="Sender"
          value={formData.sender}
          onChange={handleChange}
          className="input-field"
          required
        />
        <input
          type="text"
          name="receiver"
          placeholder="Receiver"
          value={formData.receiver}
          onChange={handleChange}
          className="input-field"
          required
        />
        <input
          type="text"
          name="title"
          placeholder="Title"
          value={formData.title}
          onChange={handleChange}
          className="input-field"
          required
        />
        <input
          type="text"
          name="description"
          placeholder="Description"
          value={formData.description}
          onChange={handleChange}
          className="input-field"
          required
        />
        <div className="container-folder">
  <div className="folder">
    <div className="front-side">
      <div className="tip"></div>
      <div className="cover"></div>
    </div>
    <div className="back-side cover"></div>
  </div>
  <label className="custom-file-upload">
    <input className="title" type="file"
          onChange={handleFileChange}
          required/>
    Choose a file
  </label>
</div>
        <input
          type="file"
          onChange={handleFileChange}
          required
          className="file-input"
        />
        <button type="submit" className="submit-button">Submit Transaction</button>
      </form>
    </div>
  );
}

export default TransactionForm;
