import React, { useState } from 'react';
import axios from 'axios';

const DocumentViewer = () => {
    const [documentHash, setDocumentHash] = useState('');
    const [fileUrl, setFileUrl] = useState(null);
    const [error, setError] = useState('');

    const handleFetchDocument = async () => {
        setError('');
        try {
            const response = await axios.get(`http://localhost:5000/get_document/${documentHash}`, {
                responseType: 'blob' // Important for handling binary data
            });

            // Create a URL for the retrieved file
            const url = window.URL.createObjectURL(new Blob([response.data]));
            setFileUrl(url);
        } catch (err) {
            setError('Document not found or error occurred.');
        }
    };

    return (
        <div>
            <h1>Document Viewer</h1>
            <input
                type="text"
                value={documentHash}
                onChange={(e) => setDocumentHash(e.target.value)}
                placeholder="Enter Document Hash"
            />
            <button onClick={handleFetchDocument}>Fetch Document</button>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {fileUrl && (
                <div>
                    <h2>Document:</h2>
                    <a href={fileUrl} target="_blank" rel="noopener noreferrer">Download Document</a>
                </div>
            )}
        </div>
    );
};

export default DocumentViewer;
