import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { byPrefixAndName } from '@awesome.me/kit-KIT_CODE/icons'

const DocumentList = () => {
    const [documents, setDocuments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [fileToUpdate, setFileToUpdate] = useState(null);
    const [selectedDocument, setSelectedDocument] = useState(null);

    const fetchDocuments = async () => {
        try {
            const response = await axios.get('http://localhost:5000/get_documents');
            setDocuments(response.data.documents);
        } catch (err) {
            setError('Error fetching documents');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchDocuments();
    }, []);

    const viewDocument = (document_hash) => {
        const url = `http://localhost:5000/get_document/${document_hash}`;
        window.open(url, '_blank');
    };

    const handleFileChange = (event) => {
        setFileToUpdate(event.target.files[0]);
    };

    const handleUpdate = async (document) => {
        if (!fileToUpdate) return;

        const formData = new FormData();
        formData.append('file', fileToUpdate);
        formData.append('sender', 'UserA');
        formData.append('receiver', 'UserB');
        formData.append('title', document.title);
        formData.append('description', 'Updated file');
        formData.append('original_hash', document.document_hash);

        try {
            // Update document transaction
            const response = await axios.post('http://localhost:5000/update_transaction', formData);
            alert(response.data.message);

            // Mine a new block
            const mineResponse = await axios.get('http://localhost:5000/mine');
            alert(mineResponse.data.message); // New block mined response

            // Reset the file input and selected document
            setFileToUpdate(null);
            setSelectedDocument(null);
            await fetchDocuments(); // Refresh the documents list to show the updated data

        } catch (error) {
            console.error('Error updating document:', error);
            alert("There was an error submitting the transaction!");
        }
    };

    if (loading) return <div>Loading documents...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div className="Alldocuments-container">
            <h1 className="alldocumenth1">All Documents</h1>
            <ul className="ulh1">
                {documents.map((doc) => (
                    <li key={doc.document_hash} className="ALLdocument-item">
                        <div className="Alldocument-details">
                        <button onClick={() => viewDocument(doc.document_hash)}>
                            <FontAwesomeIcon icon="fa-solid fa-folder" />
                            {doc.title}
                        </button>
                        <button onClick={() => setSelectedDocument(doc)}>Edit</button>
                        </div>
                    </li>
                ))}
            </ul>
            {selectedDocument && (
                <div>
                    <h2>Update Document: {selectedDocument.title}</h2>
                    <input type="file" onChange={handleFileChange} />
                    <button onClick={() => handleUpdate(selectedDocument)}>Submit Update</button>
                </div>
            )}
        </div>
    );
};

export default DocumentList;
