import React, { useEffect, useState } from 'react';
import axios from 'axios';

const DocumentList = () => {
    const [documents, setDocuments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedDocument, setSelectedDocument] = useState(null);

    useEffect(() => {
        const fetchDocuments = async () => {
            try {
                const response = await axios.get('http://localhost:5000/get_documents');
                setDocuments(response.data.documents);
            } catch (err) {
                setError('Error fetching documents');
            } finally {
                setLoading(false);
            }
        };

        fetchDocuments();
    }, []);

    const viewDocument = async (document_hash) => {
        try {
            const response = await axios.get(`http://localhost:5000/get_document/${document_hash}`, {
                responseType: 'blob', // Important to handle binary data
            });
            const fileURL = URL.createObjectURL(new Blob([response.data]));
            setSelectedDocument(fileURL);
        } catch (err) {
            setError('Error fetching document');
        }
    };

    if (loading) {
        return <div>Loading documents...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div>
            <h1>Uploaded Documents</h1>
            <ul>
                {documents.map((doc) => (
                    <li key={doc.document_hash}>
                        <button onClick={() => viewDocument(doc.document_hash)}>
                            {doc.title}
                        </button>
                    </li>
                ))}
            </ul>
            {selectedDocument && (
                <div>
                    <h2>Document Preview</h2>
                    <iframe
                        src={selectedDocument}
                        style={{ width: '100%', height: '600px' }}
                        title="Document Preview"
                    />
                </div>
            )}
        </div>
    );
};

export default DocumentList;
