import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ViewRequests = () => {
    const [requests, setRequests] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchRequests();
    }, []);

    const fetchRequests = async () => {
        try {
            const response = await axios.get('http://localhost:5000/get_document_requests');
            setRequests(response.data.requests);
            setLoading(false);
        } catch (err) {
            console.error('Error details:', err);
            setError('Error fetching requests');
            setLoading(false);
        }
    };

    const handleStatusUpdate = async (requestId, newStatus) => {
        try {
            await axios.post('http://localhost:5000/update_request_status', {
                requestId,
                status: newStatus
            });
            fetchRequests(); // Refresh the list
        } catch (err) {
            setError('Error updating request status');
        }
    };

    if (loading) return <div>Loading requests...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div className="requests-container">
            <h2>Document Requests</h2>
            <div className="requests-grid">
                {requests.map((request) => (
                    <div key={request.id} className="request-item">
                        <div className="request-header">
                            <h3>{request.request_type}</h3>
                            <span className={`status-badge ${request.status}`}>
                                {request.status}
                            </span>
                        </div>
                        <div className="request-details">
                            <p><strong>From:</strong> {request.sender}</p>
                            <p><strong>Date:</strong> {new Date(request.timestamp * 1000).toLocaleString()}</p>
                            <p><strong>Description:</strong></p>
                            <p className="request-description">{request.description}</p>
                        </div>
                        <div className="request-actions">
                            <button 
                                onClick={() => handleStatusUpdate(request.id, 'approved')}
                                className="approve-btn"
                                disabled={request.status !== 'pending'}
                            >
                                Approve
                            </button>
                            <button 
                                onClick={() => handleStatusUpdate(request.id, 'rejected')}
                                className="reject-btn"
                                disabled={request.status !== 'pending'}
                            >
                                Reject
                            </button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ViewRequests;
