import React, { useEffect, useState } from 'react';
import axios from 'axios';

const OrderByDate = () => {
  const [documents, setDocuments] = useState([]);
  
  useEffect(() => {
    // Fetch documents from the blockchain server
    axios.get('http://localhost:5000/get_documents_by_date')
      .then(response => {
        setDocuments(response.data.documents);
      })
      .catch(error => {
        console.error("There was an error fetching the documents!", error);
      });
  }, []);

  return (
    <div>
      <h2>Documents Ordered by Date</h2>
      {documents.length > 0 ? (
        <ul>
          {documents.map((doc, index) => (
            <li key={index}>
              <strong>Title:</strong> {doc.title}<br />
              <strong>Description:</strong> {doc.description}<br />
              <strong>Date:</strong> {new Date(doc.timestamp * 1000).toLocaleString()}<br />
            </li>
          ))}
        </ul>
      ) : (
        <p>No documents found.</p>
      )}
    </div>
  );
};

export default OrderByDate;
