import React, { useState } from 'react';
import axios from 'axios';

function TransactionForm() {
  const [formData, setFormData] = useState({
    sender: '',
    receiver: '',
    document_hash: '',
    title: '',
    description: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      console.log("Sending data:", formData);
      
      // Submit transaction
      const response = await axios.post('http://localhost:5000/add_transaction', formData);
      alert(response.data.message); // Transaction added response

      // Now, mine the new block
      const mineResponse = await axios.get('http://localhost:5000/mine');
      alert(mineResponse.data.message); // New block mined response
    } catch (error) {
      console.error("There was an error submitting the transaction!", error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="sender"
        placeholder="Sender"
        value={formData.sender}
        onChange={handleChange}
      />
      <input
        type="text"
        name="receiver"
        placeholder="Receiver"
        value={formData.receiver}
        onChange={handleChange}
      />
      <input
        type="text"
        name="document_hash"
        placeholder="Document Hash"
        value={formData.document_hash}
        onChange={handleChange}
      />
      <input
        type="text"
        name="title"
        placeholder="Title"
        value={formData.title}
        onChange={handleChange}
      />
      <input
        type="text"
        name="description"
        placeholder="Description"
        value={formData.description}
        onChange={handleChange}
      />
      <button type="submit">Submit Transaction</button>
    </form>
  );
}

export default TransactionForm;
