import React, { useState } from 'react';
import axios from 'axios';

const DocumentManager = () => {
    const [title, setTitle] = useState('');
    const [file, setFile] = useState(null);
    const [documentHash, setDocumentHash] = useState('');

    const handleFileChange = (event) => {
        setFile(event.target.files[0]);
    };

    const handleUpload = async () => {
        if (!file || !title) return;

        const reader = new FileReader();
        reader.onloadend = async () => {
            const base64Content = reader.result.split(',')[1];  // Get base64 string

            try {
                const response = await axios.post('http://localhost:5000/upload_document', {
                    title: title,
                    content: base64Content
                });
                setDocumentHash(response.data.document_hash);
            } catch (error) {
                console.error('Error uploading document:', error);
            }
        };
        reader.readAsDataURL(file);
    };

    const handleViewDocument = async () => {
        if (!documentHash) return;

        window.open(`http://localhost:5000/get_document/${documentHash}`);
    };

    return (
        <div>
            <h2>Document Manager</h2>
            <input
                type="text"
                placeholder="Document Title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
            />
            <input
                type="file"
                accept="application/pdf"
                onChange={handleFileChange}
            />
            <button onClick={handleUpload}>Upload Document</button>
            <button onClick={handleViewDocument}>View Document</button>
        </div>
    );
};

export default DocumentManager;
