import React, { useState, useEffect } from 'react';
import axios from 'axios';


const StudentDocuments = () => {
    const [documents, setDocuments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [documentHash, setDocumentHash] = useState('');
    const [error, setError] = useState('');
    const username = localStorage.getItem('username');

    useEffect(() => {
        fetchStudentDocuments();
    }, [username]);

    const fetchStudentDocuments = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get(`http://localhost:5000/get_documents`, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });
            
            const userDocuments = response.data.documents.filter(
                doc => doc.receiver === username
            );
            setDocuments(userDocuments);
        } catch (error) {
            console.error('Error fetching documents:', error);
            if (error.response?.status === 403) {
                window.location.href = '/login';
            }
        } finally {
            setLoading(false);
        }
    };

    const handleHashInput = (e) => {
        setDocumentHash(e.target.value);
        setError('');
    };

    const handleDownload = async () => {
        if (!documentHash.trim()) {
            setError('Please enter a document hash');
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const response = await axios.get(`http://localhost:5000/get_document/${documentHash}`, {
                headers: {
                    'Authorization': `Bearer ${token}`
                },
                responseType: 'blob'
            });

            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', `document-${documentHash}.pdf`);
            document.body.appendChild(link);
            link.click();
            link.remove();
            setDocumentHash('');
        } catch (error) {
            console.error('Error downloading document:', error);
            setError('Error downloading document. Please check the hash and try again.');
        }
    };

    if (loading) {
        return <div className="loading">Loading your documents...</div>;
    }

    return (
        <div className="student-documents-container">
            <h2>Your Requested Documents</h2>
            
            <div className="document-hash-section">
                <h3>Download Document by Hash</h3>
                <div className="hash-input-container">
                    <input
                        type="text"
                        value={documentHash}
                        onChange={handleHashInput}
                        placeholder="Enter Document Hash"
                        className="hash-input"
                    />
                    <button onClick={handleDownload} className="download-hash-button">
                        <i className="fas fa-download"></i> Download
                    </button>
                </div>
                {error && <p className="error-message">{error}</p>}
            </div>

            <div className="documents-list">
                <h3>Recent Documents</h3>
                {documents.length > 0 ? (
                    <div className="documents-grid">
                        {documents.map((doc, index) => (
                            <div key={index} className="document-card">
                                <div className="document-icon">
                                    <i className="fas fa-file-alt"></i>
                                </div>
                                <div className="document-info">
                                    <h3>{doc.title}</h3>
                                    <p className="document-added">Document Added to Blockchain!</p>
                                    <p className="document-hash">
                                        <strong>Document Hash:</strong>
                                        <span>{doc.document_hash}</span>
                                    </p>
                                    <p className="document-date">
                                        Added on: {new Date(doc.timestamp * 1000).toLocaleDateString()}
                                    </p>
                                </div>
                            </div>
                        ))}
                    </div>
                ) : (
                    <div className="no-documents">
                        <i className="fas fa-folder-open"></i>
                        <p>No documents have been uploaded for your requests yet.</p>
                    </div>
                )}
            </div>
        </div>
    );
};

export default StudentDocuments;
