import React, { useEffect, useState } from 'react';
import axios from 'axios';

const DocumentList = () => {
    const [documents, setDocuments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedDocument, setSelectedDocument] = useState(null);

    useEffect(() => {
        const fetchDocuments = async () => {
            try {
                const response = await axios.get('http://localhost:5000/get_documents');
                setDocuments(response.data.documents);
            } catch (err) {
                setError('Error fetching documents');
            } finally {
                setLoading(false);
            }
        };

        fetchDocuments();
    }, []);

    const viewDocument = (document_hash) => {
        setSelectedDocument(`http://localhost:5000/get_document/${document_hash}`);
    };

    if (loading) {
        return <div>Loading documents...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div>
            <h1>Uploaded Documents</h1>
            <ul>
                {documents.map((doc) => (
                    <li key={doc.document_hash}>
                        <button onClick={() => viewDocument(doc.document_hash)}>
                            View {doc.title}
                        </button>
                    </li>
                ))}
            </ul>
            {selectedDocument && (
                <div>
                    <h2>Document Preview</h2>
                    {selectedDocument.endsWith('.pdf') ? (
                        <iframe
                            src={selectedDocument}
                            title="Document Preview"
                            style={{ width: '100%', height: '600px' }}
                        />
                    ) : (
                        <a href={selectedDocument} download>
                            Download Document
                        </a>
                    )}
                </div>
            )}
        </div>
    );
};

export default DocumentList;
