import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';


const StudentNotifications = () => {
    const [notifications, setNotifications] = useState([]);
    const username = localStorage.getItem("username");

    const fetchNotifications = useCallback(async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get(`http://localhost:5000/get_notifications/${username}`, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });
            setNotifications(response.data.notifications);
        } catch (error) {
            console.error('Error fetching notifications:', error);
            if (error.response?.status === 403) {
                window.location.href = '/login';
            }
        }
    }, [username]);

    useEffect(() => {
        fetchNotifications();
        const interval = setInterval(fetchNotifications, 30000);
        return () => clearInterval(interval);
    }, [fetchNotifications]);

    const copyToClipboard = (hash) => {
        navigator.clipboard.writeText(hash);
        alert('Document hash copied to clipboard!');
    };

    return (
        <div className="student-notifications-container">
            <h2>Your Document Notifications</h2>
            <div className="notifications-list">
                {notifications.map((notification, index) => (
                    <div key={index} className="notification-card">
                        <div className="notification-header">
                            <i className="fas fa-file-alt"></i>
                            <h3>{notification.documentTitle}</h3>
                        </div>
                        <div className="notification-body">
                            <p><strong>Status:</strong> Document Added to Blockchain</p>
                            <p><strong>From:</strong> {notification.sender}</p>
                            <p><strong>Time:</strong> {new Date(notification.timestamp * 1000).toLocaleString()}</p>
                            <div className="hash-container">
                                <p><strong>Document Hash:</strong></p>
                                <code>{notification.documentHash}</code>
                                <button 
                                    onClick={() => copyToClipboard(notification.documentHash)}
                                    className="copy-button"
                                >
                                    <i className="fas fa-copy"></i> Copy Hash
                                </button>
                            </div>
                            <p className="info-text">
                                ℹ️ Use this hash to verify and retrieve your document using the Verify Document page
                            </p>
                        </div>
                    </div>
                ))}
                {notifications.length === 0 && (
                    <p className="no-notifications">No notifications found</p>
                )}
            </div>
        </div>
    );
};

export default StudentNotifications;
